const showStates = {};
// 管理图片列表的对象
const imageLists = {};

// 批量初始化，假设需要 7 组（根据实际数量调整）
for (let i = 1; i <= 7; i++) {
  // 初始化开关，默认 true
  showStates[`show${i}`] = ref(true);
  // 初始化图片列表为空数组
  imageLists[`list${i}`] = ref([]);
}

