<!DOCTYPE html>
<html lang="zh">
<head>
  <meta charset="UTF-8" />
  <title>简易聊天 - 不定高自动底部</title>
  <style>
    #box {
      height: 100vh;
      overflow-y: auto;
      border: 1px solid #ccc;
      padding: 10px;
    }
    .msg {
      margin: 4px 0;
      padding: 6px 8px;
      background: #f2f2f2;
      border-radius: 4px;
    }
  </style>
</head>
<body>
  <div id="box"></div>

  <script>
    const box = document.getElementById('box');

    // 滚动到底部
    function toBottom() {
      box.scrollTop = box.scrollHeight;
    }

    // 加一条消息
    function addMsg(text) {
      const div = document.createElement('div');
      div.className = 'msg';
      div.textContent = `${new Date().toLocaleTimeString()} - ${text}`;
      box.appendChild(div);
      toBottom();          // 关键：新增后立刻到底
    }

    // 初始 20 条
    for (let i = 1; i <= 20; i++) addMsg(`历史消息 ${i}`);

    // 每秒模拟新消息
    setInterval(() => addMsg('新消息'), 1000);
  </script>
</body>
</html>

