const commentCards = document.getElementsByClassName('comment-card');
for (let i = 0; i < commentCards.length; i++) {
    const avatar = commentCards[i].querySelector('.comment-avatar');
    if (avatar) {
        const index = commentCards.length - 1 - i;

        // ç§»é™¤å·²å­˜åœ¨çš„ç¼–å·ï¼ˆé˜²æ­¢é‡å¤æ·»åŠ ï¼‰
        const existingIndex = avatar.querySelector('.index-badge');
        if (existingIndex) existingIndex.remove();

        const badge = document.createElement('div');
        badge.className = 'index-badge';
        badge.textContent = index;
        badge.style.cssText = `
            position: absolute;
            top: -10px;
            right: -10px;
            background: linear-gradient(135deg, #ff4757, #ff3742);
            color: white;
            border-radius: 50%;
            width: 24px;
            height: 24px;
            display: flex;
            align-items: center;
            justify-content: center;
            font-size: 12px;
            font-weight: bold;
            border: 2px solid white;
            box-shadow: 0 2px 8px rgba(0,0,0,0.3);
            z-index: 10;
        `;

        avatar.style.position = 'relative';
        avatar.appendChild(badge);
    }
}
